func:
var current = flow.get("pipeline_temperature");
var new_val = msg.data.pipeline.temperature
if (flow.get("pipeline_damage") != true){
    if ((Math.abs(current - new_val))<=10) {
        msg.payload = new_val;
    } else {
        msg.payload = current;
    }
} else {
    msg.payload = 30;
    flow.set("pipeline_temperature", 30);
}
return msg;

var current = flow.get("pipeline_damage");
if (msg.data.public_transport.occupancy%17==0) {
    flow.set("pipeline_damage", true);
    msg.payload = true;
} else {
    msg.payload = current;
}
return msg;

var current = flow.get("water_supply_hot_pipe_temperature");
var new_val = msg.data.water_supply.hot_pipe_temperature;
if (flow.get("water_supply_damage") != true){
    if ((Math.abs(current - new_val))<=5) {
        msg.payload = new_val;
    } else {
        msg.payload = current;
    }
} else {
    msg.payload = 40;
    flow.set("water_supply_hot_pipe_temperature", 40);
}
return msg;

var current = flow.get("water_supply_cold_pipe_temperature");
var new_val = msg.data.water_supply.cold_pipe_temperature;
if (flow.get("water_supply_damage") != true){
    if ((Math.abs(current - new_val))<=1) {
        msg.payload = new_val;
    } else {
        msg.payload = current;
    }
} else {
    msg.payload = 1;
    flow.set("water_supply_cold_pipe_temperature", 1);
}
return msg;

var current = flow.get("water_supply_damage");
var new_val = msg.data.water_supply.damage
if (msg.data.public_transport.occupancy%11==0) {
    flow.set("water_supply_damage", true);
    msg.payload = true;
} else {
    msg.payload = current;
}
return msg;

var current = flow.get("water_consumption_hot_water");
var new_val = msg.data.water_consumption.hot_water;
if (flow.get("water_supply_damage")!=true){
    new_val = current + new_val;
    flow.set("water_consumption_hot_water", new_val);
    msg.payload = new_val;
} else {
    msg.payload = current;
}
return msg;

var current = flow.get("water_consumption_cold_water");
var new_val = msg.data.water_consumption.cold_water;
if (flow.get("water_supply_damage")!=true){
    new_val = current + new_val;
    flow.set("water_consumption_cold_water", new_val);
    msg.payload = new_val;
} else {
    msg.payload = current;
}
return msg;

var current = flow.get("energy_consumption");
var new_val = msg.data.energy_consumption;
new_val = current + new_val;
new_val = (parseInt(new_val * 100)) / 100;
flow.set("energy_consumption", new_val);
msg.payload = new_val;
return msg;

var current = flow.get("power_grid_volt");
var new_val = msg.data.power_grid.volt;
if ((Math.abs(current - new_val))<=15) {
    flow.set("power_grid_volt", new_val);
    msg.payload = new_val;
} else {
    msg.payload = current;
}
return msg;

var current = flow.get("sewage_system_pressure");
var new_val = msg.data.sewage_system.pressure;
if ((Math.abs(current - new_val))<=3) {
    msg.payload = new_val;
} else {
    msg.payload = current + 1;
}
return msg;

var current = flow.get("road_condition_temperature");
var new_val = msg.data.road_condition.temperature;
if ((Math.abs(current - new_val))<=10) {
    msg.payload = new_val;
} else {
    msg.payload = current;
}
return msg;

var current = flow.get("road_condition_precipitation_thickness");
var new_val = msg.data.road_condition.precipitation_thickness;
if ((Math.abs(current - new_val))<=2) {
    msg.payload = new_val;
} else {
    msg.payload = current;
}
return msg;

var current = flow.get("IP_camera_alarm");
var new_val = msg.data.IP_camera.alarm;
if ((msg.data.public_transport.occupancy)%13==0) {
    flow.set("IP_camera_alarm", true);
    msg.payload = true;
} else {
    msg.payload = current;
}
return msg;

var current = flow.get("sound_level");
var new_val = msg.data.sound_level;
if ((Math.abs(current - new_val))<=12) {
    msg.payload = new_val;
} else {
    msg.payload = current;
}
return msg;

var current = flow.get("air_quality");
var new_val = msg.data.air_quality;
if ((Math.abs(current - new_val))<=25) {
    msg.payload = new_val;
} else {
    msg.payload = current;
}
return msg;

flow.set("pipeline_temperature",msg.data.pipeline.temperature);
flow.set("pipeline_damage",false);
flow.set("water_supply_hot_pipe_temperature",msg.data.water_supply.hot_pipe_temperature);
flow.set("water_supply_cold_pipe_temperature",msg.data.water_supply.cold_pipe_temperature);
flow.set("water_supply_damage",false);
flow.set("water_consumption_hot_water",msg.data.water_consumption.hot_water);
flow.set("water_consumption_cold_water",msg.data.water_consumption.cold_water);
flow.set("energy_consumption",msg.data.energy_consumption);
flow.set("power_grid_volt",msg.data.power_grid.volt);
flow.set("sewage_system_pressure",msg.data.sewage_system.pressure);
flow.set("trash_can_fullness",msg.data.trash_can.fullness);
flow.set("public_transport_occupancy",msg.data.public_transport.occupancy);
flow.set("road_condition_temperature",msg.data.road_condition.temperature);
flow.set("road_condition_precipitation_thickness",msg.data.road_condition.precipitation_thickness);
flow.set("IP_camera_alarm",false);
flow.set("sound_level",msg.data.sound_level);
flow.set("air_quality",msg.data.air_quality);
return msg;

if (msg.data.public_transport.occupancy%17==0) {
    msg.payload = "На участке теплотрассы произошла деформация. Возможно возникновение протечки. Бригада монтажников вызвана на место аварии.";
    return msg;
}

if (msg.data.public_transport.occupancy%11==0) {
    msg.payload = "На участке водопровода произошла деформация. Возможно возникновение протечки. Бригада монтажников вызвана на место аварии.";
    return msg;
}

if (msg.payload == '0') {
    msg.payload = "Получена команда остановки мониторинга. Показатели отражают последнюю итерацию.";
    return msg;
}

if ((msg.data.public_transport.occupancy)%13==0) {
    msg.payload = "Камера распознала преступника! На местоположение вызван наряд полиции.";
    return msg;
}

var new_val = msg.data.power_grid.ampere * flow.get("power_grid_volt");
msg.payload = (parseInt(new_val * 100)) / 100;
return msg;
